package cadenas;

public class ManejoCadenas {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String St1 = "Cabeza";
		
		String St2 = " de huevo";
		
		System.out.println("Concatena: " + St1 + St2);
		
		System.out.println("Nueva linea:  \n" + St1 +" "+ St2);
		System.out.println("Tabular: \t" + St1 +" "+ St2);
		System.out.println("Retroceder: \b " + St1 +" "+ St2 + "\b");
		System.out.println("Retorno CArro: \r " + St1 +" "+ St2);
		System.out.println("Comilla Simple: \' " + St1 +" "+ St2);
		System.out.println("Comilla Doble:  \"" + St1 +" "+ St2);

	}

}
