package variables;

public class ManejoVariablesTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		boolean bool1;
		bool1= true;
		
		boolean bool2;
		bool2= false;
		
		System.out.println("Valor boolean 1 = "+ bool1);
		System.out.println("Valor boolean 2 = "+ bool2);
		System.out.println("");
		
		
		byte b1=10;
		
		byte b2= 0xa;
		
		System.out.println("Valor byte 1 = "+ b1);
		System.out.println("Valor byte 2 = "+ b2);
		System.out.println("");
		
		
		float f1=10 , f2= 22.3F;
		

		
		System.out.println("Valor float 1 = "+ f1);
		System.out.println("Valor float 2 = "+ f2);
		System.out.println("");
		
		double d1=11.0 , d2= 30.15D;
		
		System.out.println("Valor double 1 = "+ d1);
		System.out.println("Valor double 2 = "+ d2);
		System.out.println("");
		
		
		
	}

}
